CREATE TABLE IF NOT EXISTS `ad` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `TypeID` int(11) NOT NULL,  `MerchantID` int(11) NOT NULL,  `Name` varchar(200) COLLATE utf8_unicode_ci NOT NULL,  `ImageURL` varchar(500) COLLATE utf8_unicode_ci NOT NULL,  `DateExpiry` datetime NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=1 ;
