CREATE TABLE IF NOT EXISTS `listing_photo` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `ListingID` int(11) NOT NULL,  `Description` longtext COLLATE utf8_unicode_ci NOT NULL,  `ImageURL` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `Cover` int(2) NOT NULL,  `Position` int(2) NOT NULL,  `Enabled` int(2) NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=1 ;
