<?phpfunction familyTree($array){    if (is_array($array) === TRUE) {        for ($index = 0; $index < $array['count']; $index++) {            if ($array[$index]['ID'] == $_SESSION['member_AgentGroup']['param']['Agent']) {                $selected = 'selected="selected"';            }            $data = '<option style="padding-left:' . $array[$index]['Padding'] . 'px;" value="' . $array[$index]['ID'] . '" ' . $selected . '>' . $array[$index]['Name'] . ' - ' . $array[$index]['ID'] . ' | ' . $array[$index]['Company'] . '</option>';            echo $data;            unset($selected);            familyTree($array[$index]['Child']);        }    }}?><?php if ($_GET['param'] === "successd") { ?>    <div class="notify">Thank you. Your transaction has been requested successfully and is under processing. Your funds        are currently being verified and will be credited to your account within 5 - 10 minutes.    </div><?php } elseif ($_GET['param'] === "successw") { ?>    <div class="notify">Thank you. Your transaction has been requested successfully and is under processing. The amount        will be banked in to your designated bank account within 10 minutes.    </div><?php } elseif ($_GET['param'] === "successt") { ?>    <div class="notify">Thank you. Your transaction has been requested successfully and is under processing. Your funds        are currently being verified and will be transferred within 5 - 10 minutes.    </div><?php } elseif ($_GET['param'] === "failure") { ?>    <div class="error">Transaction error occurred.</div><?php } ?><?phpif(isset($_GET['page'])){    $page=$_GET['page'];}else{    $page='all';}?><div id="search_box" <?php if ($data['content_param']['search'] == "off") { ?>class="search_initial"<?php } ?>>    <script type="text/javascript">        //<![CDATA[        $(function() {            jQuery("#search_trigger").click(function(){                $('#search_content').slideTo